<?phprequire_once __DIR__ . "/../../config.php";require_login();global $PAGE, $CFG, $OUTPUT;$PAGE->set_context(\context_system::instance());$PAGE->set_url($CFG->wwwroot . '/local/learningcompanions/chat.php');$defaultlayout = 'base';$layout = optional_param('layout', $defaultlayout, PARAM_TEXT);$layoutwhitelist = ['base', 'standard', 'course', 'incourse', 'popup', 'embedded'];if (!in_array($layout, $layoutwhitelist)) {    $layout = $defaultlayout;}$PAGE->set_pagelayout($layout);$PAGE->set_title(get_string('learninggroups', 'local_learningcompanions'));$cmid = optional_param('cmid', 0, PARAM_INT);$courseid = optional_param('courseid', 0, PARAM_INT);$PAGE->requires->js_call_amd('local_learningcompanions/learningcompanions_chat', 'init');echo $OUTPUT->header();echo $OUTPUT->footer();