<?phpfunction local_learningcompanions_extend_settings_navigation() {    global $PAGE;    if (!isloggedin()){        return;    }    $config = get_config('local_learningcompanions');    $params = array(        $config->button_css_selector,        $config->button_bg_color,        $config->button_text_color,        $config->button_radius,    );    $PAGE->requires->js_call_amd('local_learningcompanions/learningcompanions', 'init', $params);}